admins = {
  'dcavalca' => '1001',
  'phil' => '1002',
  'bwann' => '1003',
  'ilan' => '1004',
  'bala' => '1005',
  'jhoblitt' => '1006',
  'ron' => '1007',
  'leiz' => '1008',
  'btmash' => '1009'
}

admins.each do |user, uid|
  user user do
    uid uid
    group 'users'
    home "/home/#{user}"
    manage_home true
    shell '/bin/bash'
  end
end

group 'sudo' do
  members admins.keys
  system true
  gid 101
end

node.default['scale_sudo']['users']['%sudo'] = 'ALL=NOPASSWD: ALL'

